import axios from 'axios';

// Create an Axios instance with the base URL configured
const instance = axios.create({
  baseURL: 'http://localhost:514'
});

export default {
  getTasks: async () => {
    const result = await instance.get('/items'); // Use the baseURL automatically
    return result.data;
  },

  addTask: async (name) => {
    try {
      const response = await instance.post('/items', { name });
      return response.data;
    } catch (error) {
      console.error('Error adding task:', error);
      throw error;
    }
  },

  setCompleted: async (id, isComplete) => {
    try {
      const response = await instance.put(`/items/${id}`, { isComplete });
      return response.data;
    } catch (error) {
      console.error('Error updating task:', error);
      throw error;
    }
  },

  deleteTask: async (id) => {
    try {
      await instance.delete(`/items/${id}`);
      return {}; // Or return a success message if needed
    } catch (error) {
      console.error('Error deleting task:', error);
      throw error;
    }
  }
};
