import axios from 'axios';
import { AxiosDefaults } from 'axios';
const apiUrl = "http://localhost:5148"
import api from './api';
export default {
  getTasks: async () => {
    const result = await api.get(`/items`)    
    return result.data;
  },

  addTask: async (name) => {
    try {
      const response = await axios.post(`${apiUrl}/items`, { name });
      return response.data; // Assuming server returns the created task
    } catch (error) {
      console.error('Error adding task:', error);
      throw error;
    }
  },

  setCompleted: async (id, isComplete) => {
    try {
      const response = await axios.put(`${apiUrl}/items/${id}`, { isComplete });
      return response.data; // Assuming server returns the updated task
    } catch (error) {
      console.error('Error updating task:', error);
      throw error;
    }
  },


  deleteTask: async (id) => {
    try {
      await axios.delete(`${apiUrl}/items/${id}`);
      return {}; // Or return a success message if needed
    } catch (error) {
      console.error('Error deleting task:', error);
      throw error;
    }
  }
};
